import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

/* -------------------------------------------------------
   üß≠ LEFT SIDEBAR NAVBAR
------------------------------------------------------- */
function SidebarNav() {
  const navItems = [
    { id: "popular", label: "üçõ Popular Dishes" },
    { id: "recent", label: "‚≠ê Recently Rated" },
    { id: "recommended", label: "üî• Recommended" },
    { id: "restaurants", label: "üè¨ Restaurants" },
  ];

  return (
    <div className="fixed left-0 top-0 h-full w-56 bg-white shadow-xl p-6 flex flex-col border-r">
      <h2 className="text-2xl font-bold text-orange-700 mb-6">Dashboard</h2>

      <nav className="flex flex-col gap-4">
        {navItems.map((item) => (
          <a
            key={item.id}
            href={`#${item.id}`}
            className="text-lg font-medium text-gray-700 hover:text-orange-600 transition"
          >
            {item.label}
          </a>
        ))}
      </nav>
    </div>
  );
}

/* -------------------------------------------------------
   MAIN DASHBOARD
------------------------------------------------------- */
export default function Dashboard() {
  const [popular, setPopular] = useState([]);
  const [recent, setRecent] = useState([]);
  const [recommended, setRecommended] = useState([]);
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [searchQuery, setSearchQuery] = useState("");
  const [dietFilter, setDietFilter] = useState("");
  const [priceSort, setPriceSort] = useState("");
  const [ratingSort, setRatingSort] = useState("");

  const user = {
    name: "Vedika Sharma",
    favorite_dish: "Paneer Tikka",
    location: "Pune, India",
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const [homeRes, recRes] = await Promise.all([
          fetch("http://127.0.0.1:5000/home"),
          fetch("http://127.0.0.1:5000/recommend", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ favorite_dish: user.favorite_dish }),
          }),
        ]);

        const homeData = await homeRes.json();
        const recData = await recRes.json();

        /* ---------- FIXED popular mapping ---------- */
      setPopular(
          (homeData.featured_dishes || []).map((item) => ({
            food_name: item.dish_name || item.name || item.title || item.food_name || "Unknown Dish",
            restaurant_name: item.restaurant_name || item.location,
            image: item.img_url || item.image,
            price: item.price || item.average_cost,
            rating: item.rating,
            diet_type: item.diet_type || "Unknown",
          }))
        );

        setRecent(
          (recData.recently_rated || []).map((item) => ({
            food_name: item.dish_name || item.name,
            restaurant_name: item.restaurant_name || item.location,
            image: item.img_url,
            price: item.average_cost,
            rating: item.rating,
            diet_type: item.diet_type || "Unknown",
          }))
        );

        setRecommended(
          (recData.recommendations || []).map((item) => ({
            food_name: item.dish_name || item.name,
            restaurant_name: item.restaurant_name || item.location,
            image: item.img_url,
            price: item.price || item.average_cost,
            rating: item.rating,
            diet_type: item.diet_type || "Unknown",
          }))
        );

       setRestaurants([
          {
            name: "Dominos",
            location: "Koregaon Park, Pune",
            rating: 4.5,
            image: "data:image/jpeg;base64,",
          },
          {
            name: "KFC",
            location: "Baner, Pune",
            rating: 4.3,
            image:"data:image/jpeg;base64,",
          },
          {
            name: "Raj",
            location: "FC Road, Pune",
            rating: 4.6,
            image: "data:image/jpeg;base64,",
          },
        ]);

        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("‚ùå Failed to load dashboard data.");
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  /* ---------------------------------------------------
      FILTER LOGIC
  --------------------------------------------------- */
  function applyFilters(list) {
    let filtered = [...list];

    if (searchQuery.trim() !== "") {
      filtered = filtered.filter((item) =>
        item.food_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.restaurant_name?.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (dietFilter !== "") {
      filtered = filtered.filter(
        (item) => item.diet_type?.toLowerCase() === dietFilter
      );
    }

    if (priceSort === "low-high") {
      filtered.sort((a, b) => (a.price || 0) - (b.price || 0));
    } else if (priceSort === "high-low") {
      filtered.sort((a, b) => (b.price || 0) - (a.price || 0));
    }

    if (ratingSort === "high-low") {
      filtered.sort((a, b) => (b.rating || 0) - (a.rating || 0));
    } else if (ratingSort === "low-high") {
      filtered.sort((a, b) => (a.rating || 0) - (b.rating || 0));
    }

    return filtered;
  }

  if (loading) return <div className="text-center mt-20">Loading‚Ä¶</div>;
  if (error) return <div className="text-center mt-20 text-red-600">{error}</div>;

  return (
    <>
      <SidebarNav />

      <div className="ml-60 min-h-screen bg-gradient-to-br from-orange-50 to-orange-100 py-12 px-6 flex flex-col items-center">
        <h1 className="text-5xl font-extrabold text-orange-800 mb-10">
          üëã Welcome back, {user.name}!
        </h1>

        {/* SEARCH + FILTER BAR */}
        <div className="w-full max-w-5xl mb-10 bg-white shadow-lg rounded-2xl p-6">
          <div className="flex flex-col md:flex-row gap-4 items-center">
            <input
              type="text"
              placeholder="Search dishes, restaurants..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-4 py-3 rounded-xl border border-orange-300"
            />

            <button
              onClick={() => setSearchQuery("")}
              className="px-6 py-3 bg-orange-600 text-white rounded-xl"
            >
              Clear
            </button>
          </div>

          <div className="flex flex-wrap gap-4 mt-5">
            <select
              value={dietFilter}
              onChange={(e) => setDietFilter(e.target.value)}
              className="px-4 py-3 rounded-xl border"
            >
              <option value="">Diet Type</option>
              <option value="vegetarian">Vegetarian</option>
              <option value="non-veg">Non-Veg</option>
            </select>

            <select
              value={priceSort}
              onChange={(e) => setPriceSort(e.target.value)}
              className="px-4 py-3 rounded-xl border"
            >
              <option value="">Sort Price</option>
              <option value="low-high">Low ‚Üí High</option>
              <option value="high-low">High ‚Üí Low</option>
            </select>

            <select
              value={ratingSort}
              onChange={(e) => setRatingSort(e.target.value)}
              className="px-4 py-3 rounded-xl border"
            >
              <option value="">Sort Rating</option>
              <option value="high-low">High ‚Üí Low</option>
              <option value="low-high">Low ‚Üí High</option>
            </select>
          </div>
        </div>

        {/* FIXED SECTION IDS */}
        <Section id="popular" title="üçõ Popular Dishes" data={applyFilters(popular)} />
        <Section id="recent" title="‚≠ê Recently Rated" data={applyFilters(recent)} />
        <Section id="recommended" title="üî• Recommended For You" data={applyFilters(recommended)} />

        <RestaurantSection id="restaurants" title="üè¨ Restaurants Near You" data={restaurants} />
      </div>
    </>
  );
}

/* ---------------------------------------------------
   DISH SECTION
--------------------------------------------------- */
function Section({ id, title, data }) {
  return (
    <section id={id} className="w-full max-w-6xl mb-14 scroll-mt-20">
      <h2 className="text-3xl font-bold text-orange-800 mb-6">{title}</h2>

      <motion.div
        className="grid sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8"
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        {data.slice(0, 4).map((dish, i) => (
          <Link 
            key={i}
            to={`/dish/${encodeURIComponent(dish.food_name)}`}
          >
            <motion.div
              className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-2xl transition cursor-pointer"
              whileHover={{ scale: 1.03 }}
            >
              <img src={dish.image} alt={dish.food_name} className="h-48 w-full object-cover" />
              <div className="p-4 text-left">
                <h3 className="text-xl font-semibold text-orange-700 mb-1">
                  {dish.food_name}
                </h3>
                <p><strong>Restaurant:</strong> {dish.restaurant_name}</p>
                <p><strong>Price:</strong> ‚Çπ{dish.price}</p>
                <p><strong>Rating:</strong> {dish.rating} / 5</p>
                <p><strong>Diet:</strong> {dish.diet_type}</p>
              </div>
            </motion.div>
          </Link>
        ))}
      </motion.div>
    </section>
  );
}

/* ---------------------------------------------------
   RESTAURANT SECTION
--------------------------------------------------- */
function RestaurantSection({ id, title, data }) {
  return (
    <section id={id} className="w-full max-w-6xl mb-20 scroll-mt-20">
      <h2 className="text-3xl font-bold text-orange-800 mb-6">{title}</h2>

      <motion.div
        className="grid sm:grid-cols-2 md:grid-cols-3 gap-8"
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        {data.slice(0, 4).map((r, i) => (
          <motion.div
            key={i}
            className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition"
            whileHover={{ scale: 1.03 }}
          >
            <img src={r.image} alt={r.name} className="h-48 w-full object-cover" />
            <div className="p-4 text-left">
              <h3 className="text-xl font-semibold text-orange-700 mb-1">{r.name}</h3>
              <p>{r.location}</p>
              <p><strong>Rating:</strong> {r.rating} / 5</p>
            </div>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
}
